import com.eviware.soapui.support.XmlHolder
def addreq = testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["AddEmployeeStep"].getPropertyValue("Request")

def id = testRunner.testCase.testSuite.testCases["GetEmployee"].getProperty("id")

def dept = testRunner.testCase.getPropertyValue("dept")

def xmlAdd = new XmlHolder(addreq)

xmlAdd.setNodeValue("//typ:addEmployee/typ:id","34")

xmlAdd.setNodeValue("//typ:addEmployee/typ:name","asmitakpatilfgjgyjhh")


xmlAdd.setNodeValue("//typ:addEmployee/typ:Department",dept)

xmlAdd.setNodeValue("//typ:addEmployee/typ:age","22")

def newAddXML = xmlAdd.getXml();

testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["AddEmployeeStep"].setPropertyValue("Request",newAddXML)

import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

def tsAdd = testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["AddEmployeeStep"]

def contextAdd = new WsdlTestRunContext(tsAdd)

tsAdd.run(testRunner,contextAdd)


def vresponse = testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["AddEmployeeStep"].getPropertyValue("Response")

def vGetRes = new XmlHolder(vresponse)

def vNameRes = vGetRes.getNodeValue("//ns:return")

assert vNameRes=="true"

